import { 
	CHANGE_SEARCH_FIELD,
	REQUEST_ROBOTS_PENDING,
	REQUEST_ROBOTS_SUCCESS,
	REQUEST_ROBOTS_FAILED

 } from './constants.js'

 
const intialState = {
	searchField: ''
}

export const searchRobots = (state=intialState, action={}) => {
	switch(action.type) {
		case CHANGE_SEARCH_FIELD: 
		return Object.assign({}, state, {searchField:action.payload});
		default:
		return state;

	}

}
